import React, { useState } from 'react'
import {useDispatch, useSelector} from "react-redux";
import TopPath from '../top-path/TopPath';
import './basket.sass';

export const Basket = () => {
  const dispatch = useDispatch();
  const state = useSelector(state => state);
  const arr = Array.from(new Set(state.map(item => JSON.stringify(item)))).map(JSON.parse); // товары

  const pathL = [
    {
      id: 1, 
      name: 'Главная',
      path: '/'
    }
  ]

  return (
    <div className='basket-page'>
      <TopPath path={pathL} productName='Корзина' pageName='Корзина'/>
      <div className="basket-page__container">
        <ul className="basket-page__steps">
          <li className='basket-step'>
            <span className='basket-step__num'>1</span>
            <p>Корзина</p>
          </li>
          <li className='basket-step'>
            <span className='basket-step__num'>2</span>
            <p>Оформление заказа</p>
          </li>
          <li className='basket-step'>
            <span className='basket-step__num'>3</span>
            <p>Заказ</p>
          </li>
        </ul>
      </div>
    </div>
  )
}
