import React, { useState, useEffect } from 'react'
import {Link, useParams} from 'react-router-dom'
import { aromas } from '../shop/Shop'
import './tovar.sass'

const Tovar = () => {
    const {path} = useParams()
    const [currentItem, setCurrentItem] = useState()
    const [nextTovar, setNextTovar] = useState(null);
    const [prevTovar, setPrevTovar] = useState(null);

    useEffect(() => {
        setCurrentItem(aromas.find(aroma => aroma.path === path));
    }, [])

    useEffect(() => {
        let prev = aromas[currentItem?.id-1];
        let next = aromas[currentItem?.id];
        setNextTovar(next ? next : null);
        setPrevTovar(prev ? prev : null);
    }, [currentItem])

  return (
      <div className='tovar-page'>
          <div className='tovar-page__header'>
              <p className='tovar-page__title'>Магазин</p>
              <ul className='tovar-page__path'>
                  <li>
                      <Link to='/'>Главная</Link>
                  </li>
                  <li>
                      <Link to='/shop'>Магазин</Link>
                  </li>
                  <li>
                      <Link to={currentItem?.unPath.path}>{currentItem?.unPath.name}</Link>
                  </li>
                  <li>
                      <p>{currentItem?.name}</p>
                  </li>
              </ul>
              <ul className='tovar-page__contacts'>
                  <li className='tovar-page-contacts__vk'><Link to='https://vk.com/lescontesfrance'><img src={require('../../assets/shop/tovar/vk-contact.svg').default} alt=""/></Link></li>
                  <li className='tovar-page-contacts__ok'><Link to='https://ok.ru/group/54769873584135'><img src={require('../../assets/shop/tovar/ok-contacts.svg').default} alt=""/></Link></li>
              </ul>
          </div>
          <div className='tovar-page__tovar'>
            <div className='tovar-page-tovar__container'>
                <div className='tovar-page__navigation-tovars'>
                    <Link disabled to={`/shop/${prevTovar?.path}`}>prev</Link>
                    <Link to={`/shop/${nextTovar?.path}`}>next</Link>
                </div>
            </div>
          </div>
      </div>
  )
}

export default Tovar
