import React, { useState } from 'react'
import {useDispatch, useSelector} from "react-redux";

export const Basket = () => {
  const dispatch = useDispatch();
  const state = useSelector(state => state);
  const arr = Array.from(new Set(state.map(item => JSON.stringify(item)))).map(JSON.parse)
  return (
    <div>
      {
        arr.map(item => (
            <>
              <p>{item.currentItem.name}</p>
              <p>{item.length}</p>
              <button data-id={item.currentItem.id} onClick={() => dispatch({type: 'REMOVE', payload: item.currentItem.id})} >delete</button>
            </>
        ))
      }
    </div>
  )
}
