import React, { useEffect, useState } from 'react'
import {useDispatch, useSelector} from "react-redux";
import { Link } from 'react-router-dom';
import TopPath from '../top-path/TopPath';
import './basket.sass';
import Tovar from './basket__/Tovar';

export const Basket = () => {
  const dispatch = useDispatch();
  const state = useSelector(state => state);
  const [arr, setArr] = useState(Array.from(new Set(state.map(item => JSON.stringify(item)))).map(JSON.parse)); // товары
  const [quentityTovars, setQuentityTovars] = useState(1);

  const staps = [
    {
      id: 1,
      name: 'Корзина',
      active: true
    },
    {
      id: 2,
      name: 'Оформление заказа',
      active: false
    },
    {
      id: 3,
      name: 'Заказ',
      active: false
    },
  ]

  const pathL = [
    {
      id: 1, 
      name: 'Главная',
      path: '/'
    }
  ]

  const decrementFunc = (currentItem) => {
      if (quentityTovars <= 1) {
          console.log()
      } else {
          setQuentityTovars(prev => --prev);
      }
  }

  const incrementFunc = (e, currentItem, length) => {
    const filtered = arr.filter(item => item.currentItem.id === currentItem.id);
    // setArr(arr.map(item => item.currentItem.id === currentItem.id ? item.length++ : item.length))
    setArr(arr.map(item => item.currentItem.id === currentItem.id ? {currentItem: currentItem, length: item.length++} : {currentItem: currentItem, length: item.length}));
    console.log(arr.map(item => item.currentItem.id === currentItem.id ? {currentItem: currentItem, length: item.length++} : {currentItem: currentItem, length: item.length}))
    // setArr(arr.map(item => [...arr, {currentItem: item.currentItem, length: item.length++}]));
    filtered.length++;
    // console.log(e.currentTarget.parentNode.childNodes[1].value++)
      if (length >= currentItem.quantity) {
          console.log();
      } else {
          // setArr(arr.map(item => item.currentItem.id === currentItem.id ? item.length++ : item.length))
          // arr.map(item => item.currentItem.id === currentItem.id ? [...arr, {currentItem: currentItem, length: item.length++}] : {currentItem: currentItem, length: item.length});
          // setQuentityTovars(prev => ++prev);
      }
  }

  useEffect(() => {
    console.log(arr)
  }, [arr])

  return (
    <div className='basket-page'>
      <TopPath path={pathL} productName='Корзина' pageName='Корзина'/>
      <div className="basket-page__container">
        <ul className="basket-page__steps">
          {
            staps.map(item => (
              <li key={item.id} className='basket-step'>
                <span className={item.active ? 'basket-step__num basket-step__num-active' : 'basket-step__num'}>{item.id}</span>
                <p>{item.name}</p>
                {
                  item.id === staps.length
                  ? null
                  : <img src={require('../../assets/shop/tovar/free-icon-right-arrow-angle-54833.png')} alt="" />
                }
              </li>
            ))
          }
        </ul>
        <div className="basket-page__table">
          <div className="basket-table-left">
            <div className="basket-table-top">
              <p className='basket-tovar-title'>ТОВАР</p>
              <p className='basket-tovar-price'>ЦЕНА</p>
              <p className='basket-tovar-col'>КОЛИЧЕСТВО</p>
              <p className='basket-tovar-pod'>ПОДЫТОГ</p>
            </div>
            <div className="basket-table-products">
              {
                arr.map(({currentItem, length}) => (
                  <Tovar currentItem={currentItem} length={length}/>
                ))
              }
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
